mapper.select:
select cm.crew_member_id, cm.user_id, cm.role_id, u.full_name, u.nickname, u.profile_img from s2.crew_member_tb cm left outer join s2.user_tb u on (u.user_id = cm.user_id) where cm.crew_id = #{crewId}
AND ( u.full_name LIKE CONCAT('%', #{searchText}, '%') OR u.nickname LIKE CONCAT('%', #{searchText}, '%') )
order by cm.role_id asC, cm.crew_member_id asC limit #{startIndex}, #{size}

mapper.update:
update crew_member_tb
set role_id = 3
where crew_id = #{crewId}
and user_id = #{userId}
and role_id = 4

mapper.delete:
delete from crew_member_tb
where crew_id = #{crewId}
and user_id = #{userId}
and role_id != 2
