mapper.update:
update welcome_tb
set status = '승인'
where crew_id = #{crewId}
    and user_id = #{userId}

update welcome_tb
set status = '거절'
where welcome_id = #{welcomeId}

mapper.select:
select exists (
        select
            1
        from
            welcome_tb
        where
            crew_id = #{crewId}
            and user_id = #{userId}
        ) as exist
